import sys
import rds_config
import pymysql
import json
import logging



#rds settings
rds_host  = "locationdb.cmccwbwsq4vf.us-east-2.rds.amazonaws.com"
name = rds_config.db_username
password = rds_config.db_password
db_name = rds_config.db_name
port = rds_config.db_port

logger = logging.getLogger()
logger.setLevel(logging.INFO)


try:
    conn = pymysql.connect(rds_host, user=name, passwd=password, db=db_name, port=port)
except Exception as e:
    logger.error("ERROR: Unexpected error: Could not connect to MySQL instance.",e)
    sys.exit()

logger.info("SUCCESS: Connection to RDS MySQL instance succeeded")

def handler(event, context):
    state = event['state']

    abbreviated_query = """ select CITY as City,
               COUNTY as County,
               LONGITUDE as Longitude,
               LATITUDE as Latitude
               from US_STATES
               inner join US_CITIES on US_STATES.ID = US_CITIES.ID_STATE
               where STATE_CODE = %s;"""

    full_query =  """ select CITY as City,
               COUNTY as County,
               LONGITUDE as Longitude,
               LATITUDE as Latitude
               from US_STATES
               inner join US_CITIES on US_STATES.ID = US_CITIES.ID_STATE
               where STATE_NAME = %s;"""

    cur = conn.cursor()
    if len(state) < 3:
        cur.execute(abbreviated_query,(state, ))
    else:
        cur.execute(full_query,(state, ))

    row_headers=[x[0] for x in cur.description]
    rv = cur.fetchall()
    json_data=[]
    for result in rv:
        json_data.append(dict(zip(row_headers,result)))

    return json_data


